import React, { useEffect, useState } from 'react';
import "./style.scss";
import { useParams } from 'react-router-dom'
function DetailProduct({ item }) {
  const { id } = useParams();
  console.log("id" + id);

  const [detailData, setdetailData] = useState([])

  useEffect(() => {
    async function fetDetail() {
      try {
        const requestURl = `http://localhost:8080/product/${id}`;
        const response = await fetch(requestURl);
        const responseJson = await response.json();
        const data = responseJson;
        setdetailData(data)
        console.log(data)
      } catch (error) {
        console.error(error.message)
      }
    }
    fetDetail();
  }, []);
  return (
    <div className="detail">
      <div className="grid__row">
        <div className="detail__content">
          <div className="product-item-img" style={{ backgroundImage: `url(${detailData[0].image})` }}>
          </div>
          <div className="detail__content--name">
            <h2>{detailData[0].name}</h2>
          </div>
          <div className="detail__content--price">
            <p>{detailData[0].price} vnđ</p>
            <p>{detailData[0].rate} Rating</p>
            <p>{detailData[0].number_access} Lượt truy cập</p>
          </div>
          <div className="detail__content--des">
            <p>{detailData[0].des}</p>
          </div>
        </div>
      </div>
    </div >
  );
}

export default DetailProduct;
