import logo from './logo.svg';
import './App.css';
import Header from './Header/Header';
import Footer from './Footer/Footer';
import Cate from './Cate/Cate';
import Product from './Product/Product';
import SortFilter from './SortFilter/SortFilter';
import DetailProduct from './DetailProduct/DetailProduct';
import { useState, useEffect } from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

function App() {
  const [productData, setProductData] = useState([])

  useEffect(() => {
    async function fetUser() {
      try {
        const requestURl = "http://localhost:8080/product";
        const response = await fetch(requestURl);
        const responseJson = await response.json();
        const data = responseJson;
        setProductData(data)
        console.log(data)
      } catch (error) {
        console.error(error.message)
      }
    }
    fetUser();
  })

  return (
    <div className="main">
      <Header />

      <div className="container">
        <div className="grid ">
          <div className="grid__row container__padding">
            <div className="grid__col-2">
              <Cate />
            </div>
            <div className="grid__col-10">
              <SortFilter />
              <Product list={productData} />
              {!productData &&
                <DetailProduct item={productData[0]} />
              }
            </div>
          </div>

        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
