import React, { useEffect, useState } from 'react';
import "./style.scss";
import { useParams } from 'react-router-dom'
function DetailProduct({ item }) {
  const { id } = useParams();
  console.log("id" + id);

  const [detailData, setdetailData] = useState([])

  useEffect(() => {
    async function fetDetail() {
      try {
        const requestURl = `http://localhost:8080/product/${id}`;
        const response = await fetch(requestURl);
        const responseJson = await response.json();
        const data = responseJson;
        setdetailData(data)
        console.log(data)
      } catch (error) {
        console.error(error.message)
      }
    }
    fetDetail();
  }, []);
  return (
    <div className="detail">
      <div className="grid__row">
        {
          detailData.map((item, index) => (
            <div className="detail__content col-2">
              <div className="detail__content--img" style={{ backgroundImage: `url(${item.image})` }}>
              </div>
              <div className="detail__content--body">
                <div className="detail__content--name">
                  <h2>{item.name}</h2>
                </div>
                <div className="detail__content--price">
                  <p>{item.price} vnđ</p>
                  <p>{item.rate} Rating</p>
                  <p>{item.number_access} Lượt truy cập</p>
                </div>
                <div className="detail__content--des">
                  <p>{item.des}</p>
                </div>
              </div>
            </div>
          ))
        }
      </div>
    </div >
  );
}

export default DetailProduct;
